html, body {
  height: 100%;
}

body {
  overflow-y: auto;
  font-family: Arial, sans-serif;
  line-height: 1.6;
  margin: 10px;
}

input, textarea {
  margin-right: 8px;
}

.help-block {
  font-size: 0.8em;
  color: #505967;
}

.form-group > label:first-child {
  font-weight: bold;
}

/* Apply flexbox to the parent container of the checkboxes */
.form-group .controls > div {
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 20px;
}

/* Style individual checkbox containers */
.form-group .controls .checkbox {
  flex: 1 1 calc(33.33% - 20px); /* Adjust the width and spacing */
  box-sizing: border-box; /* Ensure padding and border are included in the width */
}

/* Adjust the label inside the checkboxes */
.form-group .controls .checkbox label {
  display: flex;
  align-items: center;
}

