import jsonform from 'https://cdn.jsdelivr.net/npm/jsonform@2.2.5/+esm';
function saveForm(errors, values) {
  if (appsmith.model.lastEdit === 'user') {
    return;
  }
  if (errors) {
    $('#res').html('<p>There was an error in the form submission.</p>');
  } else {
    appsmith.updateModel({
      values: values
    });
    $('#res').html('<p>Form submitted successfully!</p>');
  }
}
appsmith.onReady(() => {
  const formConfig = appsmith.model.formConfig;
  appsmith.updateModel({
    lastEdit: 'system'
  });
  $('form').jsonForm({
    schema: formConfig.schema,
    form: formConfig.form,
    onSubmit: saveForm
  });
});
appsmith.onModelChange(() => {
  const formConfig = appsmith.model.formConfig;
  $('form').empty();
  $('form').jsonForm({
    schema: formConfig.schema,
    form: formConfig.form,
    onSubmit: saveForm
  });
});

